    return (<div>
     {drawerOpened && <Drawer onClose = {() => setDrawerOpened(false)}/> } 

      <Header items = {drawerItems} onClickDrawer = {() => setDrawerOpened(true)} />
      <BannerSale />

      <div className="main">
        <SidebarMenu />

        {products.map((obj) => (
