import './null.scss';
import './favorite.scss';
import { useState } from 'react';

export const Favorite = ({id, img, name, price, onClose, onClickAdd, items = [], onRemove }) => {
   
   const [isAdded, setIsAdded] = useState(false);

   const onClickAddDrawer = () => {
      onClickAdd({ id, img, name, price });
      setIsAdded(!isAdded)
    }

   return (

      <div className='overlay'>
         <div className='favorite'>

            <h1>Закладки</h1>


            <div className='favoriteItem'>
               {items.map(() => (
                  <CardCategorie
                  id={item.id}
                  img={item.img}
                  author={item.author}
                  name={item.name}
                  aboutShot={item.aboutShot}
                  price={item.price}
                  onClickAdd={(obj) => onAddtoDrawer(obj)}
                  onClickFavorite={(obj) => onAddtoFavorite(obj)}
               />
                  ))}
               </div>
            
            
            <button onClick={onClose}>Закрыть закладки</button>
         </div>

      </div>
   )
}
