import { AiFillHeart } from "react-icons/ai";

import { Link } from "react-router-dom";
import './null.scss';
import './cards/card.scss';
import { useEffect, useState } from 'react';



export const NewYear = (props) => {
  const [items,setItems] = useState([]);
  const [isLike, setIsLike] = useState(false);
  const onClickLike = () => {
    setIsLike(!isLike);
  }

  useEffect(() => {
    fetch('	https://api.itbook.store/1.0/search/mongodb').then((res) => {
    return res.json()
  }).then((json) => {
    setItems(json);
  });
  }, []);
  
  return (
    <div className="card">

      {items.map((props) => (
        <div >
                 
          <AiFillHeart onClick={onClickLike} className={isLike ? 'heartLike' : 'heartUnLike'}/>
                 
          <div className="cardContent" key={props.books.isbn13} >
            <img src={props.books.image} alt='image of the book' />
            <div className="cardContentText">
              <h1><Link>{props.books.title}</Link></h1>
              <Link className="cardContentShot">{props.books.subtitle}</Link>
              <p>{props.books.price}</p>
              <button>Добавить в корзину</button>
            </div>
          </div>
        </div>
      ))}

    </div>
  )
}
