import { BrowserRouter, Routes, Route, useSearchParams } from "react-router-dom";
import { BannerSale } from "./components/bannerSale";
import { CardCategorie } from "./components/cards/cardCategorie";
import { Header } from "./components/header";
import { SidebarMenu } from "./components/sidebarMenu";
import './app.scss';
import { Drawer } from "./components/drawer";
import { Footer } from "./components/footer";
import { Enter } from "./components/enter";
import { useState } from "react";
import { products } from './components/cards/productsArray';
import { BsSearch } from "react-icons/bs";




function App() {
  const [drawerOpened, setDrawerOpened] = useState(false);
  const [drawerItems, setDrawerItems] = useState([]);

  const onAddtoDrawer = (obj) => {
    setDrawerItems(prev => [...prev, obj]);
  }

  return (<div>
    {drawerOpened && <Drawer items={drawerItems} onClose={() => setDrawerOpened(false)} />}

    <Header onClickDrawer={() => setDrawerOpened(true)} />
    <BannerSale />

    <div className="main">
      <SidebarMenu />
     <div className="mainCard">
      <input placeholder=<BsSearch />></input>
        {products.map((item) => (
          <CardCategorie
            id={item.id}
            img={item.img}
            author={item.author}
            name={item.name}
            aboutShot={item.aboutShot}
            price={item.price}
            onClickAdd={(obj) => onAddtoDrawer(obj)}

          />))}
          </div>

    </div>

    <Enter />
    <Footer />

  </div>

  );
}

export default App;
