import {products} from './productsArray'

import {  Link} from "react-router-dom";
import '../null.scss';
import './card.scss';
import { useParams } from "react-router-dom";


export const Bestsellers = () => {
  const { id } = useParams();
   return (
      <div className="card">
    
       <div className="cardContent" key={id}>
         <img src={'.' + products[id].image}
        alt='image of the book' />
         <div className="cardContentText">
         <Link className="cardContentAuthor">{products[id].author}</Link>
         <h1><Link>{products[id].name}</Link></h1>
         <Link className="cardContentShot">{products[id].aboutShot}</Link>
         <p>{products[id].price} руб.</p>
         <button>Добавить в корзину</button>
         </div>  
         
       </div>
     
  
   </div>
   )
}
