import { BrowserRouter, Routes, Route, useSearchParams } from "react-router-dom";
import { BannerSale } from "./components/bannerSale";
import { CardCategorie } from "./components/cards/cardCategorie";
import { Header } from "./components/header";
import { SidebarMenu } from "./components/sidebarMenu";
import './app.scss';
import { Drawer } from "./components/drawer";
import { Favorite } from "./components/favorite";
import { Footer } from "./components/footer";
import { Enter } from "./components/enter";
import {  useState } from "react";
import { products } from './components/cards/productsArray';
import { Search } from "./components/search";





function App() {
  const [drawerOpened, setDrawerOpened] = useState(false);
  const [drawerItems, setDrawerItems] = useState([] );

  const [favoriteOpened, setFavoriteOpened] = useState(false);
  const [favoriteItems, setFavoriteItems] = useState([]);

  const [enterOpened, setEnterOpened] = useState(false);

  const [searchValue, setSearchValue] = useState('');
  const onChangeSearchInput = (event) => {
    setSearchValue(event.target.value);
  };


  const onAddtoDrawer = (obj) => {
    setDrawerItems( (prev) => [...prev, obj]);
    localStorage.setItem('drawerItems', JSON.stringify(drawerItems));

  }

  const onRemoveItem = (id) => {
    setDrawerItems( (prev) => prev.filter(item => item.id !== id));


  }




  const onAddtoFavorite = (obj) => {
    setFavoriteItems(prev => [...prev, obj]);
  }

  const onRemoveFavorite = (id) => {
    setDrawerItems( (prev) => prev.filter(item => item.id !== id));


  }


  return (<div>
    {drawerOpened && <Drawer items={drawerItems} onClose={() => setDrawerOpened(false)} onRemove={onRemoveItem} />}

    {favoriteOpened && <Favorite  items={favoriteItems} onClose={() => setFavoriteOpened(false)} onClickAdd={(obj) => onAddtoDrawer(obj)} onRemove={onRemoveItem} />}

    {enterOpened && <Enter onClose={() => setEnterOpened(false)}  />}

    <Header onClickDrawer={() => setDrawerOpened(true)} onClickFavorite={() => setFavoriteOpened(true)} onClickEnter={() => setEnterOpened(true)} />
    <BannerSale />
    <Search search={searchValue} setSearch={ setSearchValue}  onChangeSearch={  onChangeSearchInput}/>
    <div className="main">
      <SidebarMenu />
      <div className="mainCard">

        {products.filter((item) => (item.name.toLowerCase().includes(searchValue.toLowerCase())) || (item.author.toLowerCase().includes(searchValue.toLowerCase())) ).map((item) => (
          <CardCategorie
            id={item.id}
            img={item.img}
            author={item.author}
            name={item.name}
            aboutShot={item.aboutShot}
            price={item.price}
            onClickAdd={(obj) => onAddtoDrawer(obj)}
            onClickFavorite={(obj) => onAddtoFavorite(obj)}
          />))}
      </div>

    </div>


    <Footer />

  </div>

  );
}

export default App;
